function openNav() {
    document.getElementById("myTopnav").style.left = "0"
}

function closeNav() {
    document.getElementById("myTopnav").style.left = "-250px"
}

function menuoverlayOn() {
    document.getElementById("menuoverlay").style.display = "block"
}

function menuoverlayOff() {
    document.getElementById("menuoverlay").style.display = "none"
}

function addClassBody() {
    var b, c, d;
    b = document.body, c = "flow", d = b.className.split(" "), -1 == d.indexOf(c) && (b.className += " " + c)
}

function removeClassBody() {
    var a = document.body;
    a.className = a.className.replace(/\bflow\b/g, "")
}

function scrollFunction() {
    document.getElementById("backToTop").style.display = 200 < document.body.scrollTop || 200 < document.documentElement.scrollTop ? "block" : "none"
}

function topFunction() {
    document.body.scrollTop = 0, document.documentElement.scrollTop = 0
}
for (var btns = document.getElementsByClassName("dropbtn"), i = 0; i < btns.length; i++) btns[i].addEventListener("click", function() {
    var a = document.getElementsByClassName("active");
    0 < a.length && (a[0].className = a[0].className.replace(" active", "")), this.className += " active"
});
window.onclick = function(b) {
    if (!b.target.matches(".dropbtn")) {
        var c, d = document.getElementsByClassName("dropbtn");
        for (c = 0; c < d.length; c++) {
            var a = d[c];
            a.classList.contains("active") && a.classList.remove("active")
        }
    }
};
